import pyautoguiimport pyttsx3import speech_recognition as srimport pyaudioimport timerecog = sr.Recognizer()River = pyttsx3.init()with sr.Microphone() as source:    print('What Is Your First Fav Website: ')    recog.adjust_for_ambient_noise(source)    user_sound = recog.listen(source)    try:        text_conv = recog.recognize_google(user_sound)        print('You said: {}'.format(text_conv))        add_words=open("fav_webs", 'a')        add_words.write(text_conv + '.com')        add_words.close()    except:        print("Sorry Couldn't Differenciate Noise")with sr.Microphone() as source:    print('Say The Name Of Your Favorite Website: ')    recog.adjust_for_ambient_noise(source)    user_sound = recog.listen(source)    try:        text_conv = recog.recognize_google(user_sound)        print('You said: {}'.format(text_conv))        River.say('You said: {}'.format(text_conv))        River.say("Alright, Opening")        River.runAndWait()    except:        print(" Sorry can't understand what your saying")with open('fav_webs') as file:    contents = file.read()    if text_conv in contents:        time.sleep(2)        pyautogui.keyDown('command')        pyautogui.press('space')        pyautogui.keyUp('command')        pyautogui.typewrite('Chrome')        pyautogui.press('enter')        time.sleep(2)        pyautogui.hotkey('ctrl', 'command', 'f')        time.sleep(2)        pyautogui.hotkey('command', 't')        pyautogui.click(338, 57)        time.sleep(2)        pyautogui.typewrite(text_conv)        pyautogui.press('enter')    else:        print('No click found by that name')