import pyautoguiimport pyttsx3import speech_recognition as srimport pyaudioimport timerecog = sr.Recognizer()River = pyttsx3.init()with sr.Microphone() as source:    print('How may river be of assistance')    River.say("How may river be of assistance")    River.runAndWait()    recog.adjust_for_ambient_noise(source)    user_sound = recog.listen(source)    try:        text_conv = recog.recognize_google(user_sound)        print('You said: {}'.format(text_conv))        River.say('You said: {}'.format(text_conv))        River.runAndWait()        add_words = open("fav_webs", 'a')    except:        print("Sorry Couldn't Differenciate Noise")        River.say("Sorry Couldn't Differenciate Noise")        River.runAndWait()with open('fav_webs') as file:    contents = file.read()    if text_conv in contents:        time.sleep(2)        pyautogui.keyDown('command')        pyautogui.press('space')        pyautogui.keyUp('command')        pyautogui.typewrite('Chrome')        pyautogui.press('enter')        time.sleep(2)        pyautogui.hotkey('ctrl', 'command', 'f')        time.sleep(2)        pyautogui.hotkey('command', 't')        pyautogui.click(338, 57)        time.sleep(2)        pyautogui.typewrite(text_conv)        pyautogui.press('enter')    else:        print('No click found by that name')        River.say("No click found by that name")        River.runAndWait()