import pyautoguiimport pyttsx3import speech_recognition as srimport pyaudioimport timeimport sysrecog = sr.Recognizer()River = pyttsx3.init()while True:    with sr.Microphone() as source:        recog.adjust_for_ambient_noise(source)        user_sound = recog.listen(source)        try:            text_conv = recog.recognize_google(user_sound)            print('You said: {}'.format(text_conv))            if text_conv == 'River':                River.say("How may river be of assistance")                River.runAndWait()                print('How may river be of assistance')                with sr.Microphone() as source:                    recog.adjust_for_ambient_noise(source)                    user_sound = recog.listen(source)                    try:                        text_conv1 = recog.recognize_google(user_sound)                        print('You said: {}'.format(text_conv1))                        River.say('You said: {}'.format(text_conv1))                        River.runAndWait()                        if text_conv1 == 'goodbye River':                            River.say('Ok See You Later')                            River.runAndWait()                            sys.exit()                        else:                            River.say("I haven't learned that yet")                            River.runAndWait()                    except:                        sys.exit                with sr.Microphone() as sour:                    recog.adjust_for_ambient_noise(sour)                    user_sound = recog.listen(sour)                    try:                        text_conv2 = recog.recognize_google(user_sound)                        print('You said: {}'.format(text_conv2))                        if text_conv2 == 'open website':                            River.say('Which website')                            River.runAndWait()                            with sr.Microphone() as sourc1:                                recog.adjust_for_ambient_noise(sourc1)                                user_sound = recog.listen(sourc1)                                try:                                    text_conv3 = recog.recognize_google(user_sound)                                    print('You said: {}'.format(text_conv3))                                    with open('fav_webs', 'a') as f:                                        f.write(text_conv3 + '.com\n')                                        f.close()                                    with open('fav_webs') as file:                                        contents = file.read()                                    if text_conv3 in contents:                                        time.sleep(2)                                        pyautogui.keyDown('command')                                        pyautogui.press('space')                                        pyautogui.keyUp('command')                                        pyautogui.typewrite('Chrome')                                        pyautogui.press('enter')                                        time.sleep(2)                                        pyautogui.hotkey('ctrl', 'command', 'f')                                        time.sleep(2)                                        pyautogui.hotkey('command', 't')                                        pyautogui.click(338, 57)                                        time.sleep(2)                                        pyautogui.typewrite(text_conv3)                                        pyautogui.press('enter')                                    else:                                        print('No click found by that name')                                        River.say("No click found by that name")                                        River.runAndWait()                                except:                                    print('')                        else:                            print("I don't know that yet")                    except:                        print('')        except:            sys.exit()